function scaleToFit(size, bounds, ignoreRatio) {
	var newSize = Object.clone(size);
	if (ignoreRatio) {
		if (size.y >= bounds.y) { newSize.y = bounds.y; }
		if (size.x >= bounds.x) { newSize.x = bounds.x; }
	} else {
		sizeRatio = newSize.x / newSize.y;
		if (newSize.x > bounds.x) {
			newSize.x = bounds.x;
			newSize.y = newSize.x / sizeRatio;
		}
		if (newSize.y > bounds.y) {
			newSize.y = bounds.y;
			newSize.x = newSize.y * sizeRatio;
		}
	}

	newSize.x = Math.round(newSize.x);
	newSize.y = Math.round(newSize.y);
	return newSize;
}

