var NWElmt = new Class({
	nw:		null,
	elmt:	null,
	types:	[],

	typeClickingIndex:	null, // the index of the type currently being processed for click


	// ======================================================== INIT ======================================
	initialize: function(elmt, nw) {
		this.nw = nw;
		this.elmt = elmt;
		this.elmt.store('nwElmt', this);

		var typeStrings = this.elmt.get('class').match(this.nw.typesRegex); // get list of NW types
		if (!typeStrings.length) { this.throwError('No NovumWare types found on elmt'); return; } // shouldn't ever get here, but just in case

		typeStrings.each(function(typeString){
			// split params from type string
			var typeInfo = typeString.split(':');
			var typeName = typeInfo.shift().replace('NW', 'NWType');
			var typeParams = typeInfo;

			var options = {};
			if (typeParams.length) { options.subtype = typeParams[0]; }

			var TypeClass = window[typeName];
			if (typeOf(TypeClass) != 'class') { this.throwError('NovumWare type not found: '+typeName); }
			var type = new TypeClass(this, this.nw, options);

			this.types.push(type);
		}.bind(this));
	},

	
	// ======================================================== PUBLIC ======================================
	getPlugin: function(suffix) {
		return this.nw.getPlugin(suffix);
	},

	click: function() {
		this.typeClickingIndex = -1;
		this.clickNextType();
	},

	clickNextType: function() {
		this.typeClickingIndex ++;
		if (this.typeClickingIndex+1 > this.types.length) {
			this.nw.finishClickEvent();
			return;
		}

		var type = this.types[this.typeClickingIndex];
		if (type.click()) this.clickNextType();
	},

	cancelClicking: function() {
		this.nw.finishClickEvent()
	},


	// ======================================================== PROTECTED ======================================
	throwError: function(error) { this.nw.throwError(error); }

});


