var NWPluginFlashMessage = new Class({
	Extends:	NWPluginAbstract,

	Binds:	['showNextMessage'],

	options: {
		errorMessage:	'Something went wrong!'
	},

	classPositive:	'positive',
	classNegative:	'negative',

	typePositive:	'positive',
	typeNegative:	'negative',

	flashMessageElmt:	Elements.from('<div class="nw-flashMessage alert"></div>')[0],

	messageQueue:	[], // {message, type}
	showingQueue:	false,


	// ======================================================== SETUP ======================================
	setup: function() {
		this.flashMessageElmt.fadeHide().inject(document.body); // put the flash message HTML in the DOM
	},


	// ======================================================== PUBLIC ======================================
	showMessage: function(message) {
		this.addMessageToQueue(message);
		this.showMessageQueue();
	},

	showPositiveMessage: function(message) {
		this.addMessageToQueue(message, this.typePositive);
		this.showMessageQueue();
	},

	showNegativeMessage: function(message) {
		this.addMessageToQueue(message, this.typeNegative);
		this.showMessageQueue();
	},
	

	// ======================================================== PRIVATE ======================================
	addMessageToQueue: function(message, type) {
		this.messageQueue.push({
			message:	message,
			type:		type
		});
	},

	showMessageQueue: function() {
		if (this.showingQueue) return;
		this.showingQueue = true;
		this.showNextMessage();
	},

	showNextMessage: function() {
		// stop if no more messages in queue
		if (this.messageQueue.length < 1) {
			this.showingQueue = false;
			return;
		}

		var nextMessage = this.messageQueue.shift();
		this.show(nextMessage);
	},

	show: function(messageInfo) {
		// measure and position message elmt
		var flashMessageWidth;
		this.flashMessageElmt.set('html', messageInfo.message);
		this.flashMessageElmt.measure(function(){
			flashMessageWidth = this.getSize().x;
		});
		this.flashMessageElmt.setStyle('margin-left', -flashMessageWidth/2+'px');
		
		// set message class based on message type
		if (!messageInfo.type) {
			this.flashMessageElmt.removeClass(this.classPositive);
			this.flashMessageElmt.removeClass(this.classNegative);
		} else if (messageInfo.type == this.typePositive) {
			this.flashMessageElmt.removeClass(this.classNegative);
			this.flashMessageElmt.addClass(this.classPositive);
		} else if (messageInfo.type == this.typeNegative) {
			this.flashMessageElmt.removeClass(this.classPositive);
			this.flashMessageElmt.addClass(this.classNegative);
		}

		// fade message in then out
		// this.flashMessageElmt.pauseFx(500).fadeIn().pauseFx(2000).fadeOut({ link:'chain', onChainComplete:this.showNextMessage });
		// this.flashMessageElmt.fadeInPauseOut(2000, {onComplete:this.showNextMessage });
		this.flashMessageElmt.fadeInPauseOut(3000, {onChainComplete:this.showNextMessage});
	},


	// ======================================================== HELPER ======================================
	showErrorMessage: function() { this.showNegativeMessage(this.options.errorMessage); }

});


