var NWPluginHistory = new Class({
	Extends:	NWPluginAbstract,

	Binds: ['onPopstate','onNWPopupClick','onNWPopupUserClose','onContentLoaded'],

	linksSelectString:	'a:not([class*=NWLink]):not([class*=NWPopup])',

	dataContentContainerId:	'data-nwHistory-contentContainerId',

	contentContainerElmt:	null,

	windowScroll:	null, // for scrolling to named anchors

	currentState:	window.history.state,


	// ======================================================== SETUP ======================================
	setup: function() {
		this.nw.usingHistoryPlugin = true;

		if (!this.currentState) this.currentState = { url:window.location.pathname+window.location.hash } // create current state if none exists

		var elmt = $$('.NWHistory')[0];
		if (!elmt) return;

		this.contentContainerElmt = $(elmt.get(this.dataContentContainerId));
		if (!this.contentContainerElmt) this.throwError('No container element found');

		this.windowScroll = new Fx.Scroll(window),
		window.addEvent('popstate', this.onPopstate);

		elmt.addEvent('click:relay(a:not('+this.nw.selectorString+'))', this.onLinkClick.bind(this)); // setup generic delegated click listener
		// elmt.getElements(this.linksSelectString).addEvent('click', this.onLinkClick.bind(this)); // intercept certain child link clicks
	
		this.nw.eventDelegate.addEvent('nwPopupClick', this.onNWPopupClick); // listen for popup clicks to push to history
		this.nw.eventDelegate.addEvent('nwPopupUserClose', this.onNWPopupUserClose); // listen for when user closes popup to push to history
	},


	// ======================================================== PUBLIC ======================================
	setPageLocation: function(url) {
		this.pushState(url);
		this.setState(url);
	},


	// ======================================================== PRIVATE ======================================
	// push state to history stack
	pushState: function(state) {
		if (typeof state == 'string') state = {url: state};

		window.history.pushState(state, null, state.url);
	},

	// set current page state
	setState: function(state) {
		if (typeof state == 'string') state = {url: state};

		var stateHasNewBaseUrl = this.stateHasNewBaseUrl(state);
		this.currentState = state;

		if(stateHasNewBaseUrl) {
			// make ajax request for url
			var ajaxOptions = {
				url:		state.url,
				update:		this.contentContainerElmt,
				onSuccess:	this.onContentLoaded
			};
			new NWRequest.HTML(ajaxOptions);
		} else this.onContentLoaded();

		this.firePopstateEvent();
	},


	// ======================================================== PRIVATE ======================================
	stateHasNewBaseUrl: function(state) {
		var stateUri = state.url.toURI();
		var currentStateUri = this.currentState.url.toURI();
		if (stateUri.get('directory')+stateUri.get('file') == currentStateUri.get('directory')+currentStateUri.get('file')) return false;
		else return true;
	},

	namedAnchorOfUrl: function(url) {
		var indexOfAnchor = url.indexOf('#');
		if (indexOfAnchor == -1) return null;

		var namedAnchor = url.substr(indexOfAnchor+1);
		return namedAnchor;
	},

	goToNamedAnchor: function() {
		var namedAnchor = this.namedAnchorOfUrl(this.currentState.url);
		if (!namedAnchor) return;
		this.windowScroll.toElement(namedAnchor);
	},

	firePopstateEvent: function() {
		this.nw.eventDelegate.fireEvent('nwHistoryPopstate', this.currentState);
	},


	// ======================================================== EVENTS ======================================
	onLinkClick: function(event) {
		var link = event.target;
		if (link.get('download') || link.get('target') == '_blank') return; // ignore links for downloads and those with target="_blank"

		var href = link.get('href');
		if (!href) return; // ignore links with no href

		event.stop(); // prevent default link behavior

		this.setPageLocation(href);
	},

	onPopstate: function(popstateEvent) {
		var state = popstateEvent.event.state; // weird nesting

		if (state) this.setState(state);
		else this.setState({url:window.location.href});
	},

	onContentLoaded: function() {
		this.goToNamedAnchor();
		// this.firePopstateEvent();
	},

	onNWPopupClick: function(event) {
		var uri = new URI(window.location.href);
		uri.setData('nwPopup', event.target.get('href'));
		this.pushState(uri.toString());
	},

	onNWPopupUserClose: function(event) {
		var uri = new URI(window.location.href);
		uri.setData('nwPopup', null);
		this.pushState(uri.toString());
	}
	
});


