var NWRequest = new Class({

	Implements:	Options,
	Binds:		['onRequest', 'onError', 'onComplete'],

	passedOnRequest:	null,
	passedOnSuccess:	null,
	passedOnError:		null,
	passedOnComplete:	null,

	options: {
		method:				'GET',
		showLoadingImage:	true
	},

	request:	null,


	// ======================================================== PUBLIC ======================================		
	initialize: function(options) {
		if (!options.url) { $nw.throwError('No url was provided'); } // make sure a url was provided

		// save passed event handlers for invoking manually
		this.passedOnRequest =	options.onRequest;
		this.passedOnSuccess =	options.onSuccess;
		this.passedOnError =	options.onError;
		this.passedOnComplete =	options.onComplete;

		// set event handlers
		options.onRequest =		this.onRequest;
		options.onSuccess =		this.onSuccess;
		options.onError =		this.onError;
		options.onComplete =	this.onComplete;

		this.setOptions(options); // override default options

		this.makeRequest(this.options);
	},

	getRedirectLocation: function() {
		return this.request.getHeader('Location');
	},


	// ======================================================== PRIVATE ======================================
	
	// ======================================================== EVENTS ======================================
	onRequest: function() {
		if (this.passedOnRequest) this.passedOnRequest();
		if (this.options.showLoadingImage) {
			if (typeof this.options.showLoadingImage == 'object') $nw.getPlugin('LoadingImage').show(this.options.showLoadingImage);
			else $nw.getPlugin('LoadingImage').show();
		}
	},

	onError: function(xhr) {
		if (this.passedOnError) this.passedOnError(xhr);
		$nw.getPlugin('FlashMessage').showErrorMessage();
	},

	onComplete:	function() {
		if (this.options.showLoadingImage) {
			if (typeof this.options.showLoadingImage == 'object') $nw.getPlugin('LoadingImage').hide(this.options.showLoadingImage);
			else $nw.getPlugin('LoadingImage').hide();
		}
		if (this.passedOnComplete) this.passedOnComplete();
	}

});

NWRequest.HTML = new Class({

	Extends:	NWRequest,
	Binds:		['onSuccess'],

	update:	null,
	append:	null,

	options: {
		evalScripts:	false,
	},


	// ======================================================== PUBLIC ======================================		
	initialize: function(options) {
		// save passed update / append for invoking manually
		this.update =	options.update;
		options.update = null;
		this.append =	options.append;
		options.append = null;

		this.parent(options);
	},


	// ======================================================== PRIVATE ======================================
	makeRequest: function(o) {
		this.request = new Request.HTML(o);
		this.request.send();
	},


	// ======================================================== EVENTS ======================================
	onSuccess: function(responseTree, responseElements, responseHTML, responseJavaScript) {
		if (this.update) {
			$(this.update).fadeOutInjectFadeIn(responseHTML, {
				empty:			true,
				initContainer:	true,
				onComplete:		function(){
					Browser.exec(responseJavaScript);
					if (this.passedOnSuccess) this.passedOnSuccess(responseTree, responseElements, responseHTML, responseJavaScript);
				}.bind(this)
			});
		}

		if (this.append) {
			newHTML = new Elements.from(responseHTML);
			newHTML.fadeHide();
			$(this.append).adopt(newHTML);
			newHTML.each(function(newHTMLNode){ $nw.initContainer(newHTMLNode) });
			newHTML.fadeIn();
		}

		if (this.passedOnSuccess) this.passedOnSuccess(responseTree, responseElements, responseHTML, responseJavaScript);
	},

});

NWRequest.JSON = new Class({

	Extends:	NWRequest,
	Binds:		['onSuccess'],


	// ======================================================== PUBLIC ======================================		

	// ======================================================== PRIVATE ======================================
	makeRequest: function(o) {
		this.request = new Request.JSON(o);
		this.request.send();
	},


	// ======================================================== EVENTS ======================================
	onSuccess: function(responseText, responseXML) {
		// JSON is returned and valid if this is a Request.JSON
		if (responseText.nwFlashMessages) { // if there are any flash messages
			var flashMessagePlugin = $nw.getPlugin('FlashMessage');

			Object.each(responseText.nwFlashMessages, function(messages, key){
				// assume there is only one of each type of message
				if (key == 'negative') { flashMessagePlugin.showNegativeMessage(messages[0]); }
				else if (key == 'positive') { flashMessagePlugin.showPositiveMessage(messages[0]); }
				else { flashMessagePlugin.showMessage(messages[0]); }
			});
		}

		if (this.passedOnSuccess) this.passedOnSuccess(responseText, responseXML);
	},

});


